def find_ids(element):#
    tree = etree.HTML(element)
    ids=tree.xpath('//tr/td/div/div/div/span/a/@href')
    titles=tree.xpath('//tr/td/div/div/div/span/a/b/@title')
    ids=[i[-7:] for i in ids]
    titles=[i.replace('\xa0',' ') for i in titles]
    return  ids,titles
